import { useState } from "react"
import qs from 'qs'
export default function Dashbord(){
    const [searchEvent, search]= useState('')
    const [title , settitle] = useState('')
    const [description , setDescription] = useState('')
    const [date, setDate] = useState('')
    const [isRecurring , setIsRecurring] = useState(false)
    const [addeventstatus, eventstatus] = useState('')
    const addevent = async (e)=>{
        e.preventDefault()
        const AddEventData = {
            title: title,
            description : description,
            date : date,
            is_recurring : isRecurring,
            token : sessionStorage.getItem("auth_token")
        }
        try{
            const response = await fetch('http://127.0.0.1:8000/addevent/',{
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                },
                body: qs.stringify(AddEventData),
            })
            if (response.status === 200){
                eventstatus('done')
            }
            else{
                eventstatus('error')
            }
        }
        catch(error){
            alert(error)
        }
    }
    return (
        <div>
            <div class="dash-header">
                <h1>Welcome to DateMate Dashboard</h1>
            </div>

        <div className="dash-maincontainer">

            <div class="add-event-form">
            <h3>Add New Event</h3>
            <form >
                <label for="title">Event Title</label>
                <input type="text" id="title" placeholder="Event Title"
                    value={title} onChange={(e)=>settitle(e.target.value)}
                required/>

                <label for="description">Event Description</label>
                <textarea id="description" placeholder="Describe the event" required
                    value={description} onChange={(e)=>setDescription(e.target.value)}
                />

                <label for="eventDate">Event Date</label>
                <input type="date" id="eventDate" required
                    value={date} onChange={(e)=>setDate(e.target.value)}
                />

                <label for="isRecurring">Is this event recurring?</label>
                <input type="checkbox" id="isRecurring" value={isRecurring} onChange={(e)=>setIsRecurring(e.target.value)}/>

                <button type="submit" class="cta-button">Add Event</button>
            </form>
            {(addeventstatus ==='done')? <p > event added </p>:(addeventstatus === 'error')?<p>eorror</p>
            :<></>  }
            </div>
            </div>
       

        </div>
    )
}
